
public class Example_of_default_constructor {
	int id;
	String name;
	void display() {
		System.out.println(id+" "+name);
	}	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Example_of_default_constructor s1=new Example_of_default_constructor();
		Example_of_default_constructor s2=new Example_of_default_constructor();
		s1.display();
		s2.display();

	}

}
